function navigator(){document.addEventListener("scroll",()=>{var e=document.querySelectorAll("section"),t=document.querySelectorAll(".header__container--nav a");let o="";e.forEach(e=>{var t=e.offsetTop;window.scrollY>=t-e.clientHeight/3&&(o=e.id)}),t.forEach(e=>{e.classList.remove("focus"),e.getAttribute("href")==="#"+o&&e.classList.add("focus")})})}function scrollNav(){document.querySelectorAll(".header__container--nav a").forEach(e=>{e.addEventListener("click",e=>{e.preventDefault();e=e.target.getAttribute("href");document.querySelector(e).scrollIntoView({behavior:"smooth"})})})}function header(){let e=document.querySelector(".header"),t=document.querySelector(".aboutFestival");document.addEventListener("scroll",()=>{t.getBoundingClientRect().bottom<1?e.classList.add("fixed"):e.classList.remove("fixed")})}function createGallery(){var t=document.querySelector(".gallery__photo");for(let e=1;e<=16;e++){var o=document.createElement("PICTURE");o.innerHTML=`
      <source srcset="./img/gallery/thumb/${e}.avif" type="image/avif"/>
      <source srcset="./img/gallery/thumb/${e}.webp" type="image/webp"/>
      <img loading="lazy" src="./img/gallery/thumb/${e}.jpg" alt="Imagen de DJ's" />
    `,t.appendChild(o),o.onclick=()=>showImg(e)}}function showImg(e){var t=document.createElement("IMG"),o=document.querySelector("body"),l=document.createElement("DIV");t.src=`./img/gallery/full/${e}.jpg`,t.alt="Gallery IMG",o.classList.add("overflow-hidden"),l.classList.add("modal"),l.onclick=()=>closeImg(),l.appendChild(t),o.appendChild(l)}function closeImg(){let e=document.querySelector("body"),t=document.querySelector(".modal");t.classList.add("fade-out"),setTimeout(()=>{t?.remove(),e.classList.remove("overflow-hidden")},500)}document.addEventListener("DOMContentLoaded",()=>{createGallery(),header(),navigator(),scrollNav()});